	}

	public class AudiCarFactory extends AudiFactory {
		@Override
		public <T extends AudiCar> T createProduct(class<T> clz) {
		Product p = null;
		try {
			p = (AudiCar) class.forName(clz.getName()).newInstance(); 
		} catch() {
			e.printStackTrace();
		}
		return (T) p;
		}
