		public abstractvoid method();
	}
	// 具体产品类 A1
	public class ConcreteProducxtA1 extends AbstractProductA { 
		@Override
		public void method() {
			System.out.println("具体产品 A1 的方法.");
		}
	}
	// 具体产品类 A2
	public class ConcreteProducxtA2 extends AbstractProductA { 
		@Override
		public void method() {
			System.out.println("具体产品 A2 的方法.");
		}
	}
	// 具体产品类 B1
	public class ConcreteProducxtB1 extends AbstractProductB { 
		@Override
		public void method() {
			System.out.println("具体产品 B1 的方法.");
		}
	}
	// 具体产品类 B2
	public class ConcreteProducxtB2 extends AbstractProductB { 
		@Override
		public void method() {
			System.out.println("具体产品 B2 的方法.");
		}
	}
	// 抽象工厂类
	public abstract class AbstractFactory { 
		/**
		 * 创建产品 A 的方法
		 * @return 产品 A 的对象
		 */
		public abstract AbstractProductA createProductA();
		/**
		 * 创建产品 B 的方法
		 * @return 产品 B的对象
		 */
		public abstract AbstractProductB createProductB();
